function sticky_menu(e,t,s){e.style.height=t.offsetHeight+"px",pageYOffset>=s||scrollY>=s?e.classList.add("sticky"):(pageYOffset<s||scrollY<s)&&e.classList.remove("sticky")}window.onload=function(){const e=document.getElementById("shopify-section-header"),t=document.querySelectorAll("#shopify-section-header header")[0],s=document.querySelectorAll("#shopify-section-announcement-bar")[0].offsetHeight;window.addEventListener("scroll",function(){sticky_menu(e,t,s)},{passive:!1}),(pageYOffset>=s||scrollY>=s)&&(e.style.height=t.offsetHeight+"px",e.classList.add("sticky"))};