function sticky(s,e){pageYOffset>=e||scrollY>=e?s.classList.add("sticky"):(pageYOffset<e||scrollY<e)&&s.classList.remove("sticky")}window.onload=function(){const s=document.getElementById("shopify-section-header"),e=document.querySelectorAll("#shopify-section-announcement-bar")[0].offsetHeight;window.addEventListener("scroll",function(){sticky(s,e)},{passive:!1}),(pageYOffset>=e||scrollY>=e)&&s.classList.add("sticky")};