function sticky(e,s){pageYOffset>=s||scrollY>=s?e.classList.add("sticky"):(pageYOffset<s||scrollY<s)&&e.classList.remove("sticky")}window.onload=function(){const e=document.getElementById("shopify-section-header"),s=(document.querySelectorAll("#shopify-section-header header")[0],document.querySelectorAll("#shopify-section-announcement-bar")[0].offsetHeight);window.addEventListener("scroll",function(){sticky(e,s)},{passive:!1}),(pageYOffset>=s||scrollY>=s)&&e.classList.add("sticky")};